const Context = createContext('default value');
const Provider = ({children}) => {
  return (
    <Context.Provider value={'value'}>
      {children}
    </Context.Provider>
  );
};
const Container = () => {
  const context = useContext
  return (
    <div>lala</div>
  );
};

function App() {
