    const [value, setValue] = useState(false);
    const propValue = {
        value: false,
        toggle: () => setValue(!value),
    };

    return (
        <Context.Provider value={propValue}>
            {children}
        </Context.Provider>
    );
};
const Component = () => {
    const {value, toggle} = useContext(Context);

