import { useField } from "formik";

const Select = ({label, ...props}) => {
    const [field] = useField({...props, type: 'radio'});

    console.log(field);
    return (
        <div>
            <label>{label} </label>
            <select {...field} {...props} />
            {
                meta.touched && meta.error ?
                <div>{meta.error}</div> :
                null
            }
        </div>
    );
};

export default Select;
