import {useState} from 'react';

const App = () => {
    const [value, setValue] = useState({
        filed1: '',
    });
    const handleChange = (e) => {
        setValue(e.target.value);
    };

    console.log(value);

    return (
        <div>
            {
                value.length < 5 ?
                <span>The minimum length is 5</span> :
                null
            }
            <input type='text' name='filed1' value={value} onChange={handleChange}/>
            <textarea name=''/>
        </div>
    );
};

export default App;
