import {useState} from 'react';

const App = () => {
    const [value, setValue] = useState({
        field1: '',
        textArea: '',
    });
    const handleChange = (e) => {
        setValue({});
    };

    console.log(value);

    return (
        <div>
            {
                value.length < 5 ?
                <span>The minimum length is 5</span> :
                null
            }
            <input type='text' name='field1' value={value.field1} onChange={handleChange}/>
            <textarea name='textArea' onchange={handleChange} value={value.textArea} />
        </div>
    );
};

export default App;
