import {useState} from 'react';

const App = () => {
    const [value, setValue] = useState({
        field1: '',
        field2: '',
    });
    const handleChange = (e) => {
        setValue({
            ...value,
            [e.target.name]: e.target.value,
        });
    };

    console.log(value);

    return (
        <div>
            {
                value.length < 5 ?
                <span>The minimum length is 5</span> :
                null
            }
            <input type='text' name='field1' value={value.field1} onChange={handleChange}/>
            <textarea name='field2' value={value.field2} onChange={handleChange} />
            <select>
                <option value=''>-- Select --</option>
                <option value=''>Option 1</option>
                <option value=''>-- Select --</option>
                <option value=''>-- Select --</option>
            </select>
        </div>
    );
};

export default App;
