import {useRef} from 'react';

const App = () => {
    const input = useRef();
    const submit = () => {
        console.log(input.current.value);
    };
    
    return (
        <div>
            <div>
                <span>Lala</span>
                <input type='text' name='field1' ref={input}/>
            </div>
            <input type='submit' value='Send' onClick={submit} />
        </div>
    );
};

export default App;
