import {useReducer} from 'react';

// const state = {
//     items: 0,
// };
// const action = {
//     type: string, 
//     payload: any,
// };

const initialState = {
    items: 0,
};

const reducer = (state, action) => {
    switch(action.type) {
        case 'increase':
            return {
                items: state.items + 1,
            }
        case 'decrease':
            return {
                items: state.items - 1,
            }
        case 'set':
            return {
                items: action.payload,
            }
    }
};

const App = () => {
    const [state, dispatch] = useReducer(reducer, initialState);

    return (
        <p>Items: state.items</p>
    );
}

export default App;
