class Interval extends Component {
    
}

const App = () => {
    const [items, increase] = useCustomHook(0);

    useEffect(() => {
        document.title = items;
        // console.log('useEffect');
    }, [items]);

    return (
        <div>
            Items: {items}
            &nbsp;
            <button
                onClick={increase}
            >
                Add Item
            </button>
            <Iterval items={items}/>
        </div>
    );
};

export default App;
