//                 </button>
//             </div>
//         );
//     }
// }

const useCustomHook = (initialValue) => {
    const [items, setItems] = useState(initialValue);
    const increase = () => {
        setItems(items + 1);
    };

    return [items, increase];
};

const App = () => {
    const [items, increase] = useCustomHook(0);

    return (
        <div>
            Items: {items}
            &nbsp;
            <button
                onClick={increase}
            >
                Add Item
            </button>
        </div>
    );
};

export default App;
