import {useRef} from 'react';

const App = () => {
    const ref = useRef();
    const click = () => {
        console.log(ref.current.clientHeight);
        ref.current.innerHtml = 'xx';
    };

    return (
        <div 
            onClick={click}
            ref={ref}
        >
            lala
        </div>
    );
};

export default App;
