import {Component, useState, useEffect} from 'react';

const useCustomHook = (initialValue) => {
    const [items, setItems] = useState(initialValue);
    const increase = () => {
        setItems(items + 1);
    };

    return [items, increase];
};

// const Interval = (items) => {
