//         return () => clearInterval(i);
//     }, [items]);

//     return (
//         <p>Interval</p>
//     );
// };

class Interval extends Component {
    interval = '';

    componentDidMount() {
        this.interval = setInterval(() => console.log(this.props.items));
    }

    render() {
        return (
            <p>Interval</p>
        );
    }
}

const App = () => {
    const [items, increase] = useCustomHook(0);

    useEffect(() => {
        document.title = items;
        // console.log('useEffect');
    }, [items]);

    return (
        <div>
            Items: {items}
            &nbsp;
            <button
                onClick={increase}
            >
                Add Item
            </button>
            <Interval items={items}/>
        </div>
    );
};

export default App;
