import {useState, useEffect} from 'react';

const useCustomHook = (initialValue) => {
    const [items, setItems] = useState(initialValue);
    const increase = () => {
        setItems(items + 1);
    };

    return [items, increase];
};

const Iterval = (items) => {
    useEffect(() => {
        setInterval(() => console.log(items), 1000);

        

       
    }, [items]);

    return (
        <p>Interval</p>
    );
};

const App = () => {
    const [items, increase] = useCustomHook(0);

    useEffect(() => {
        document.title = items;
        // console.log('useEffect');
    }, [items]);

    return (
        <div>
            Items: {items}
            &nbsp;
            <button
                onClick={increase}
            >
                Add Item
            </button>
            <Iterval items={items}/>
        </div>
    );
};

export default App;
