import {useReducer, useState} from 'react';

// const state = {
//     items: 0,
// };
// const action = {
//     type: string, 
//     payload: any,
// };

const initialState = {
    items: 0,
};

const reducer = (state, action) => {
    switch(action.type) {
        case 'increase':
            return {
                items: state.items + 1,
            }
        case 'decrease':
            return {
                items: state.items - 1,
            }
        case 'set':
            return {
                items: action.payload,
            }
    }
};

const App = () => {
    const [state, dispatch] = useReducer(reducer, initialState);
    const [value, setValue] = useState('');

    return (
        <div>
            <p>Items: {state.items}</p>
            <input 
                value={value}
                onChange={() => setValue}
            />
            <button
                onClick={() => dispatch({type: 'increase'})}
            >
                +
            </button>
            <button
                onClick={() => dispatch({type: 'decrease'})}
            >
                -
            </button>
            <button
                onClick={() => dispatch({type: 'set', payload: 10,})}
            >
                Set 50
            </button>
       </div>
    );
}

export default App;
