import {Component} from 'react';

class Button extends Component {
   render() {
    console.log('render button');

    return (
        <button>Send</button>
    );
   };
}

class App extends Component {
    state = {
        value: 1,
    };

    render() {
        console.log(this.state);

        return (
            <div>
                <p>Hello world!!</p>
                <button
                    className={`${this.state.value}`}
                    onClick={() => this.setState({value: 2})}
                >
                    Send
                </button>
            </div>
        );
    }
}


export default App
