import {Component} from 'react';

class Input extends Component {
    state = {
        value: '',
    };

    handleChange = (value) => {
        this.setState({value: value});
    }

    render() {
        return (
            <input
                value={this.state.value}
                onChange={e => this.handleChange(e.target.value)}
            />
        );
    }
}

class App extends Component {
    state = {
        name: '',
        lastName: '',
    };

    render() {
        return (
            <p>
                Full Name:
                <Input/>
                <Input/>
            </p>
        );
    }
}

export default App;
