                <input />
            </form>
            <button onClick={() => dispatch({type: 'todo/add'})}>Mostrar todos</button>
            <button>Completados</button>
            <button>Incompletos</button>
            <ul>
                <li>todo 1</li>
                <li>todo 2</li>
            </ul>
         
        </div>
    );
};

export default App;
