import {useState} from 'react';
import {useDispatch, useSelector} from 'react-redux';


const initialState = {
    entities: [],
};

export const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'todo/add': 
          return {
            ...state,
            entities: []
          }
        case 'decrementar': 
          return state - 1;
        case 'set':
          return action.payload;
        default: 
          return state;
      }
    return state;
};

const App = () => {
    return (
        <div>
            <form>
                <input />
                <button>Mostrar todos</button>
                <button>Completados</button>
                <button>Incompletos</button>
                <ul>
                    <li>todo 1</li>
                    <li>todo 2</li>
                </ul>
            </form>
        </div>
    );
};

export default App;
