//         case 'todo/add': 
//             return {
//                 ...state,
//                 entities: state.entities.concat({...action.payload}),
//             }
//         case 'todo/complete':
//             const newTodos = state.entities.map(todo => {
//                 if (todo.id === action.payload.id) {
//                     return {...todo, completed: !todo.comlpleted}
//                 }

//                 return todo;
//             });

//            return {
//             ...state,
//             entities: newTodos,
//            };
//         case 'filter/set': 
//            return {
//             ...state,
//             filter: action.payload,
//            };
//       }

//     return state;
// };

const TodoItem = ({todo}) => {
    const dispatch = useDispatch();

    return (
        <li 
            style={{textDecoration: todo.completed ? 'line-through': 'none'}}
            onClick={() => dispatch(setComplete(todo))}
        >
            {todo.title}
        </li>
    );
};

const App = () => {
    const [value, setValue] = useState('');
    const dispatch = useDispatch();
    const todos = useSelector(selectTodos);
    const status = useSelector(selectStatus);

    const submit = e => {
        e.preventDefault();

        if (!value.trim()) {
            return;
        }

        const id = Math.random().toString(36);
        const todo= {
            title: value,
            comlpleted: false,
            id,
        };

        dispatch({type: 'todo/add', payload: todo});
        setValue('');
    };

    if (status.loading === 'pending') {
        return <p>Cargando...</p>
    }

    if (status.loading === 'rejected') {
        return <p>{status.error}</p>
    }

    return (
        <div>
            <form onSubmit={submit}>
                <input value={value} onChange={e => setValue(e.target.value)}/>
            </form>
            <button onClick={() => dispatch(setFilter('all'))}>Mostrar todos</button>
            <button onClick={() => dispatch(setFilter('complete'))}>Completados</button>
            <button onClick={() => dispatch(setFilter('incomplete'))}>Incompletos</button>
            <button onClick={() => dispatch(fetchThunk())}>Fetch</button>
            <ul>
                {
                    todos.map(
                        todo => <TodoItem key={todo.id} todo={todo} />
                    )
                }
            </ul>
         
        </div>
    );
};

export default App;
