//            };
//       }

//     return state;
// };

const selectTodos = state => {
    const {entities, filter} = state;

    if (filter === 'complete') {
        return entities.filter(todo => todo.completed);
    }

    
    if (filter === 'incomplete') {
        return entities.filter(todo => !todo.completed);
    }

    return entities;
}
;

const TodoItem = ({todo}) => {
    const dispatch = useDispatch();

    return (
        <li 
            style={{textDecoration: todo.completed ? 'line-through': 'none'}}
            onClick={() => dispatch({type: 'todo/complete', payload: todo})}
        >
            {todo.title}
        </li>
    );
};

const App = () => {
    const [value, setValue] = useState('');
    const dispatch = useDispatch();
    const todos = useSelector(selectTodos);

    const submit = e => {
        e.preventDefault();

        if (!value.trim()) {
            return;
        }

        const id = Math.random().toString(36);
        const todo= {
            title: value,
            comlpleted: false,
            id,
        };

        dispatch({type: 'todo/add', payload: todo});
        setValue('');
    };

    return (
        <div>
            <form onSubmit={submit}>
                <input value={value} onChange={e => setValue(e.target.value)}/>
            </form>
            <button onClick={() => dispatch({type: 'filter/set', payload: 'all'})}>Mostrar todos</button>
            <button onClick={() => dispatch({type: 'filter/set', payload: 'complete'})}>Completados</button>
            <button onClick={() => dispatch({type: 'filter/set', payload: 'incomplete'})}>Incompletos</button>
            <ul>
                {
                    todos.map(
                        todo => <TodoItem key={todo.id} todo={todo} />
                    )
                }
            </ul>
         
        </div>
    );
};

export default App;
