import {Component} from 'react';

const styles = {
    layout: {
        backgroudColor: '#fff',
        color: '#0A283E',
        alignItems: 'center',
        display: 'flex',
    },
};

class Layout extends Component {
    render() {
        return (
            <div style={styles.layout}>
                <div>
                    {this.props.children}
                </div>
            </div>
        );
    }
}

export default Layout;
