import Input from './Input';
import Button from './Button';

const UserForm = () => {
    const [form, handleChange, reset] = useForm({
        name: '',
        lastName: '',
        email: '',
      });
      const submit = e => {
        e.preventDefault();
          
        setUsers([
          ...users,
          form,
        ]);
    
        reset();
      };
      
    return (
        <form onSubmit={submit}>
            <Input 
            label='Name'
            name='name'
            value={form.name}
            onChange={handleChange}
            placeholder='Name'
            />
            <Input 
            label='Last Name'
            name='lastName'
            value={form.lastName}
            onChange={handleChange}
            placeholder='Last Name'
            />
            <Input 
            label='Email'
            name='email'
            value={form.email}
            onChange={handleChange}
            placeholder='Email'
            />
            <Button>Send</Button>
        </form>
    );
};

export default UserForm;
